package week2.day1;



public class FindOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int n=11;
	for (int i=2;i<=n;i++)
	{
		if(n%i==1)
		{
			System.out.println("It is an odd number");
		break;}
		else {
			System.out.println("Not an odd number");
		break;}
		}

}}
